async function fetchApi(t){let r=await fetch(t),e=await r.json();return e}const cardsHistorics=document.querySelector(".historics .cards"),datasHistorics=fetchApi("http://localhost:3001/historics-list");datasHistorics.then(t=>{t&&t.length>0?t.forEach(r=>{cardsHistorics.innerHTML+=`<article><h3>${t.date}</h3><strong class="amount">${parseInt(r.amount).toLocaleString("fr-FR")} â‚¬</strong><strong>${r.associations.split(",").join(" - ")}</strong></article>`}):cardsHistorics.innerHTML=`<p>Aucun historique recens\xe9 pour le moment.</p>`});const cardsStreamers=document.querySelector(".streamers .cards"),datasStreamers=fetchApi("http://localhost:3001/streamers-list");datasStreamers.then(t=>{t&&t.length>0?t.forEach(t=>{cardsStreamers.innerHTML+=`<article><a href="${t.twitch}" target="_blank"><div class="containerImg"><img src="/images/streamers/${t.name.toLowerCase().split(" ").join("-")}.webp" alt="Lien vers le profil de ${t.name}" ></div><h3>${t.name}</h3></a></article>`}):cardsStreamers.innerHTML=`<p>Aucun streamer recens\xe9 pour le moment.</p>`});